package com.example.hookpms;

import android.content.Context;
import android.content.pm.PackageManager;
import android.util.Log;

import java.lang.reflect.Field;
import java.lang.reflect.Method;
import java.lang.reflect.Proxy;

public class ServiceManagerWraper {

    public final static String ZXA = "zxa";

    public static void hookPMS(Context context , String signed,String appPkgName,int hashCode){
        try {


            //获取全局的ActivityThread对象
            Class<?> activityThreadClazz = Class.forName("android.app.ActivityThread");
            Method currentActivityThreadMethod = activityThreadClazz.getDeclaredMethod("currentActivityThread");
            Object currentActivityThrea = currentActivityThreadMethod.invoke(null);

            //获取ActivityThread里面原始的sPackageManager
            Field sPackageManagerField = activityThreadClazz.getDeclaredField("sPackageManager");
            sPackageManagerField.setAccessible(true);
            Object sPackageManager = sPackageManagerField.get(activityThreadClazz);

            //准备好代理对象，用来替换原始的对象
            Class<?> iPackageManagerInterface = Class.forName("android.content.pm.IPackageManager");
            Object proxy = Proxy.newProxyInstance(iPackageManagerInterface.getClassLoader(),
                    new Class<?>[]{iPackageManagerInterface},
                    new PmsHookBinderInvocationHandler(sPackageManager,signed,appPkgName,0)
                    );
            //1,替换掉ActivityThread里面的sPackageManager字段
            sPackageManagerField.set(currentActivityThrea,proxy);
            //2，替换applicationPackageManager里面的mPM对象
            PackageManager pm = context.getPackageManager();
            Field mPmField = pm.getClass().getDeclaredField("mPM");

            mPmField.setAccessible(true);
            mPmField.set(pm,proxy);
            Log.i(ZXA, "hook pms done ");
        }catch (Exception e){
           // Log.i(ZXA, "hook pms error: " + Log.getStackTraceString(e));
        }
    }

    public static void hookPMS(Context context){
        //自定义签名，和包名
        String appPkgName ="com.example.signcheck01";
        String sign = "";
        hookPMS(context,sign,appPkgName,0);
    }

}
